mapper.update:
update 
	member
set
	m_password=#{m_password}, m_password_con=#{m_password}, m_name=#{m_name}, m_phone=#{m_phone}, m_address=#{m_address} 
where
	m_id = #{m_id}

update
	member
set
	m_nickname=#{m_nickname}, m_introduction=#{m_introduction}, m_activity_area=#{m_activity_area}
where
	m_id = #{m_id}
